import React, { useEffect, useRef, useState } from 'react';

const ProjectDetailsModal = ({ projectDetails, setIsOpen }) => {
  const videoRef = useRef(null);
  const [shouldPlay, setShouldPlay] = useState(false);

  useEffect(() => {
    if (shouldPlay && videoRef.current) {
      videoRef.current.play().catch((error) => {
        console.error('Error attempting to play the video:', error);
      });
    }

    return () => {
      if (videoRef.current) {
        videoRef.current.pause();
      }
    };
  }, [shouldPlay, projectDetails]);

  useEffect(() => {
    if (projectDetails && projectDetails.type === 'video') {
      setShouldPlay(true);
    }
    return () => {
      setShouldPlay(false);
    };
  }, [projectDetails]);

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-modal" onClick={() => setIsOpen(false)}>Ã—</button>
        <h2>{projectDetails.title}</h2>
        {projectDetails.type === 'video' && (
          <div className="video-container">
            <video ref={videoRef} controls>
              <source src={projectDetails.video.mp4} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        )}
        {projectDetails.type === 'document' && (
          <div className="document-details">
            <p>{projectDetails.document.projectInfo}</p>
            <p>{projectDetails.document.technologies}</p>
            <p>Date: {projectDetails.document.date}</p>
            <a href={projectDetails.document.url.link} target="_blank" rel="noopener noreferrer">
              {projectDetails.document.url.name}
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProjectDetailsModal;
