import React, { useEffect, useRef } from "react";

import Slider from "react-slick";
import ModalVideo from "react-modal-video";

const ProjectDetailsModal = ({ projectDetails, setIsOpen }) => {
    const { type, video } = projectDetails;

    const renderVideo = () => {
      if (video.vimeo) {
        return (
          <iframe
            src={`https://player.vimeo.com/video/${video.id}`}
            width="100%"
            height="400px"
            frameBorder="0"
            allow="autoplay; fullscreen; picture-in-picture"
            allowFullScreen
          ></iframe>
        );
      } else {
        return (
          <iframe
            width="100%"
            height="400px"
            src={`https://www.youtube.com/embed/${video.id}`}
            title={projectDetails.title}
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        );
      }
    };
  
    return (
      <div className="modal">
        <div className="modal-content">
          <span className="close" onClick={() => setIsOpen(false)}>
            &times;
          </span>
          <h2>{projectDetails.title}</h2>
          {type === types.VIDEO && renderVideo()}
          {type === types.DOCUMENT && (
            <div>
              <p>{projectDetails.document.projectInfo}</p>
              <p>Technologies: {projectDetails.document.technologies}</p>
              <p>Date: {projectDetails.document.date}</p>
            </div>
          )}
        </div>
      </div>
    );
  };
