import React, { useState } from "react";
import ProjectDetailsModal from "./ProjectDetailsModal";
import library from "../../src/videos/library Management.mp4";
import medication from "../../src/videos/medication review.mp4";
import med from ".."

const Portfolio = () => {
  const [selectedProjectDetails, setSelectedProjectDetails] = useState(null);
  const [isOpen, setIsOpen] = useState(false);

  const projectsData = [
    {
      title: "Library Management",
      type: "video",
      thumbImage:"./public/images/library.jpg",
      videoSrc: library,
    },
    {
      title: "Medication Review",
      type: "video",
      thumbImage:"images/projects/medication.jpg", 
      videoSrc: medication,
    },
  ];

  const handleProjectClick = (project) => {
    setSelectedProjectDetails(project);
    setIsOpen(true);
  };

  return (
    <>
      <section id="portfolio" className="section bg-light">
        <div className="container">
          <h2 className="text-10 fw-600 text-center mb-5">
            Some of my most recent projects
          </h2>
          <div className="row g-4">
            {projectsData.map((project, index) => (
              <div
                className="col-sm-6 col-lg-4"
                key={index}
                onClick={() => handleProjectClick(project)}
              >
                <div className="portfolio-box">
                  <div className="portfolio-img">
                    <img
                      className="img-fluid"
                      src={project.thumbImage}
                      alt={project.title}
                    />
                    <div className="portfolio-overlay">
                      <h5 className="text-white">{project.title}</h5>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
      {isOpen && (
        <ProjectDetailsModal
          projectDetails={selectedProjectDetails}
          setIsOpen={setIsOpen}
        />
      )}
    </>
  );
};

export default Portfolio;
